import React from "react";
import { useState } from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";

const Dictaphone = () => {
  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const [inputText, setInputText] = useState("");

  if (!browserSupportsSpeechRecognition) {
    return <span>Browser doesn't support speech recognition.</span>;
  }

  return (
    <div>
      <div>
        <div>
          <div>
            <div className="block">
              <p className="mb-2 italic text-gray-200">{transcript}</p>
            </div>
            <div className="relative flex">
              <div>
                <input
                  type="text"
                  placeholder="Write your message ..."
                  value={inputText}
                  onChange={(e) => setInputText(e.target.value)}
                  className="w-full focus:outline-none focus:placeholder-gray-400 text-gray-600 placeholder-gray-600 pl-4 pr-28 bg-gray-200 py-3"
                ></input>
                <div className="absolute right-0 items-center inset-y-0 hidden sm:flex">
                  <button
                    type="button"
                    className="inline-flex items-center justify-center rounded-md-none px-4 py-3 transition duration-100 ease-in-out text-white bg-neutral-600 hover:bg-neutral-400 focus:outline-none"
                  >
                    <span className="font-semibold">Send</span>
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      viewBox="0 0 20 20"
                      fill="currentColor"
                      className="h-4 w-4 ml-2 transform rotate-90"
                    >
                      <path d="M10.894 2.553a1 1 0 00-1.788 0l-7 14a1 1 0 001.169 1.409l5-1.429A1 1 0 009 15.571V11a1 1 0 112 0v4.571a1 1 0 00.725.962l5 1.428a1 1 0 001.17-1.408l-7-14z"></path>
                    </svg>
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <button
          type="button"
          className="inline-flex 
          w-full mt-4 items-center justify-center rounded-none px-4 py-3
           active:bg-red-500 focus:ring focus:ring-red-300 transition duration-100 ease-in-out text-white bg-neutral-600 hover:bg-neutral-400 focus:outline-none"
          onMouseDown={() => {
            resetTranscript();
            SpeechRecognition.startListening({ continuous: true });
          }}
          onMouseUp={() => {
            SpeechRecognition.stopListening();
            setInputText(transcript);
            resetTranscript();
          }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
            className="h-6 w-6 text-white"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z"
            ></path>
          </svg>
          <span className="font-semibold">
            {listening ? "Listening . . . " : "Press to talk"}
          </span>
        </button>
        <div>
          <p>Microphone: {listening ? "on" : "off"}</p>
          <p>Message: {transcript}</p>
          <button onClick={SpeechRecognition.startListening}>Start</button>
          <button onClick={SpeechRecognition.stopListening}>Stop</button>
          <button onClick={resetTranscript}>Reset</button>
        </div>
      </div>
    </div>
  );
};
export default Dictaphone;
